mapper.select:
select nick, id, uid, phone, gender, avatar
from t_user
where phone = #{phone, jdbcType=VARCHAR}
  and pwd = #{pwd,jdbcType=VARCHAR};

select id
from t_user
where phone = #{phone, jdbcType=VARCHAR};

mapper.update:
update t_user
set nick = #{nick, jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT};
